function runNearestNeighborTest()  ca_mp_all = [];  ca_svd_all_max = [];  disp("No centering and normalization");  for i = 0:9    transformRGBImagesToGrayImages("nearest", false);    transformNearestResizedGrayImagesToBigMatrixCSV();    saveSVDofA();    [ca_mp, ca_svd] = digits();    ca_mp_all = [ca_mp_all, ca_mp];    ca_svd_all_max = [ca_svd_all_max, max(ca_svd)];  end  disp("AVG CA_MP:");  disp(mean(ca_mp_all));  disp("AVG CA_SVD:");  disp(mean(ca_svd_all_max));    ca_mp_all = [];  ca_svd_all_max = [];  disp("With centering and normalization");  for i = 0:9    transformRGBImagesToGrayImages("nearest", true);    transformNearestResizedGrayImagesToBigMatrixCSV();    saveSVDofA();    [ca_mp, ca_svd] = digits();    ca_mp_all = [ca_mp_all, ca_mp];    ca_svd_all_max = [ca_svd_all_max, max(ca_svd)];  end  disp("centered AVG CA_MP:");  disp(mean(ca_mp_all));  disp("centered AVG CA_SVD:");  disp(mean(ca_svd_all_max));end